import bg from "./assets/background.jpg";
import "./App.css";
import Navbar from "./Components/Navbar";
import React from "react";
import { useEffect, useState, useRef } from "react";
import AboutMe from "./Components/AboutMe";
import Projects from "./Components/Projects";
import Contact from "./Components/Contact";
import TypewriterComponent from "typewriter-effect";

function App() {
  const [scroll, setScroll] = useState(0);
  const aboutRef = useRef(null);
  const projectsRef = useRef(null);
  const contactRef = useRef(null);

  function handleScroll() {
    const container_top = document
      .querySelector(".container")
      .getBoundingClientRect().top;
    if (container_top > 0) {
      setScroll(window.scrollY);
    } else {
      setScroll(window.innerHeight);
    }
  }

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="App pb-16">
      <img
        src={bg}
        className="background"
        alt="portfolio background"
        style={{ filter: `blur(${scroll / 200}px)` }}
      />
      <Navbar
        aboutRef={aboutRef}
        projectsRef={projectsRef}
        contactRef={contactRef}
      />
      <div className="main-header relative">
        <div className="welcome-text">
          <h1>
            Welcome!
            <div className="block text-[2rem] font-light">
              <TypewriterComponent
                onInit={(typewriter) => {
                  typewriter
                    .typeString("I'm Sarang, an aspiring AI Software Developer")
                    .pauseFor(2000)
                    .deleteAll()
                    .typeString("Looking for Summer internship positions in 2024!")
                    .start();
                }}
                options={{
                  autoStart: true,
                }}
              />

            </div>
          </h1>
        </div>
      </div>
      <div className="container relative flex flex-col m-auto max-w-[60%] rounded-[14px] pt-12 pb-12 pr-[6.25rem] pl-[6.25rem] gap-8 opacity-90 justify-center bg-[#DFDFDF]">
        <AboutMe aboutRef={aboutRef} />
        <Projects projectsRef={projectsRef} />
        <Contact contactRef={contactRef} />
      </div>
    </div>
  );
}

export default App;
