import { useEffect, useState } from "react";
import "../Stylesheets/Navbar.css";
export default function Navbar({ aboutRef, projectsRef, contactRef }) {
  const [isSmall, setIsSmall] = useState(false);
  const [isDark, setIsDark] = useState(false);

  const scrollToRef = (ref) => {
    const topPosition = ref.current.getBoundingClientRect().top + window.scrollY - 100; 
    window.scrollTo({
        top: topPosition,
        behavior: 'smooth'
    });
};

  const handleScroll = () => {
    if (window.scrollY > 25) {
        setIsDark(true);
    }else{
        setIsDark(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  });

  return (
    <>
      <div
      id="navbar"
        class={`fixed w-full flex flex-row justify-between pl-8 pr-8 pt-3 pb-4 ${
          isDark ? "bg-black bg-opacity-50 backdrop backdrop-blur-md text-white outline outline-white outline-1" : ""
        }`}
      >
        <div className="logo font-bold">Sarang</div>
        <ul className="links flex flex-row gap-8 cursor-pointer">
          <li onClick={() => scrollToRef(aboutRef)}>About</li>
          <li onClick={() => scrollToRef(projectsRef)}>Projects</li>
          <li onClick={() => scrollToRef(contactRef)}>Contact Me</li>
        </ul>
      </div>
    </>
  );
}
