import pfp from "../assets/aboutme_pic.jpg";
import { logos } from "../constants";
import "../Stylesheets/AboutMe.css" 
import Header from "./Header";

export default function AboutMe({ aboutRef }) {
  return (
    <>
      <div className="about-me" ref={aboutRef}>
        <Header headerName="About Me"/>
        <img className="profile-pic w-[240px] h-[240px] rounded-[]" src={pfp} alt="profile pic" />
        <div className="social-media flex justify-center gap-4">
          {logos.map((logo) => {
            return (
              <a href={logo.link}>
                <img src={logo.src} alt={logo.alt} width={48} height={48}/>
              </a>
            );
          })}
        </div>
        <p>
          I am a 3rd year Computer Science student at the University of
          Waterloo. I am passionate about software development and I am
          currently looking for a co-op position for Winter 2022.
        </p>
      </div>
    </>
  );
}
